import React, { useState, useEffect } from "react";
import "./App.css";
import signupImage from "./Screenshot_2025-01-23_124650-removebg-preview.png"; // Import the image

function App() {
  const [isModalOpen, setIsModalOpen] = useState(true); // Modal is open by default
  const [isAnimating, setIsAnimating] = useState(true); // Enable animation initially

  const closeModal = () => {
    setIsAnimating(false);
    setTimeout(() => {
      setIsModalOpen(false);
    }, 300); // Match the CSS transition duration
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      document.querySelector(".modal input")?.focus();
    }, 300); // Wait for the animation to complete
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="App">
      {isModalOpen && (
        <div
          className={`overlay ${isAnimating ? "overlay-active" : ""}`}
          onClick={closeModal}
        >
          <div
            className={`modal ${isAnimating ? "modal-active" : ""}`}
            onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside the modal
          >
            <div className="form-wrapper">
              {/* Left Section: Form */}
              <div className="form-left">
                <h2>Sign up</h2>
                <form className="form-container">
                  <div className="input-group">
                    <i className="fas fa-user"></i>
                    <input type="text" placeholder="Your Name" required />
                  </div>
                  <div className="input-group">
                    <i className="fas fa-envelope"></i>
                    <input type="email" placeholder="Your Email" required />
                  </div>
                  <div className="input-group">
                    <i className="fas fa-lock"></i>
                    <input type="password" placeholder="Password" required />
                  </div>
                  <div className="input-group">
                    <i className="fas fa-key"></i>
                    <input type="password" placeholder="Repeat your password" required />
                  </div>
                  <div className="terms">
                    <input type="checkbox" id="terms" required />
                    <label htmlFor="terms">
                      I agree to all statements in <a href="#">Terms of service</a>
                    </label>
                  </div>
                  <button type="submit" className="register-btn">
                    Register
                  </button>
                </form>
              </div>
              {/* Right Section: Image */}
              <div className="form-right">
                <img src={signupImage} alt="Sign up illustration" />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
